import React from 'react';
import './App.css';
import Crossword, { useIpuz } from '@jaredreisinger/react-crossword';

const theme = {
  gridBackground: 'rgb(255, 255, 255)', // Set the grid background color to white
};

function App() {
  const data = useIpuz({
    version: 'http://ipuz.org/v2',
    kind: ['http://ipuz.org/crossword#1'],
    dimensions: { width: 3, height: 3 },
    puzzle: [
      [{ cell: 1, style: { shapebg: 'circle' } }, 2, '#'],
      [3, { style: { shapebg: 'circle' } }, 4],
      [null, 5, { style: { shapebg: 'circle' } }],
    ],
    solution: [
      ['C', 'A', '#'],
      ['B', 'O', 'T'],
      [null, 'L', 'O'],
    ],
    clues: {
      Across: [
        [1, 'OR neighbor'],
        [3, 'Droid'],
        [5, 'Behold!'],
      ],
      Down: [
        [1, "Trucker's radio"],
        [2, 'MSN competitor'],
        [4, 'A preposition'],
      ],
    },
  });

  return (
    <div className="App">
      <header className="App-header">
        <div style={{ width: '25em', display: 'flex' }}>
          <Crossword data={data} />
        </div>
      </header>
    </div>
  );
}

export default App;
