import './App.css';
import React from 'react';
import Crossword from 'react-crossword';

const data = {
  across: {
    1: {
      clue: 'one plus one',
      answer: 'TWO',
      row: 0,
      col: 0,
    },
  },
  down: {
    2: {
      clue: 'three minus two',
      answer: 'ONE',
      row: 0,
      col: 2,
    },
  },
};

function App() {
  return (
    <div className="App">
      {
        data && data.across && data.down
        ? <Crossword data={data} />
        : <div>Loading crossword...</div>
      }
    </div>
  );
}

export default App;
